import React, { useEffect, useState } from "react";
import {
  LineChart, Line, XAxis, YAxis, Tooltip, CartesianGrid, ResponsiveContainer
} from "recharts";
import { useNavigate } from "react-router-dom";

export default function Tracker({ onLogout }) {
  const [entries, setEntries] = useState([]);
  const [activity, setActivity] = useState("");
  const [emission, setEmission] = useState("");
  const [riskInfo, setRiskInfo] = useState(null); // ðŸ‘ˆ Popup control
  const navigate = useNavigate();

  useEffect(() => {
    fetch("http://localhost:5000/api/entries")
      .then((res) => res.json())
      .then((data) => setEntries(data));
  }, []);

  const getRiskCategory = (emission) => {
    if (emission < 5) {
      return { category: "Safe Usage", color: "green", advice: "You're doing great! Keep it up." };
    } else if (emission < 10) {
      return { category: "Medium Risk Usage", color: "orange", advice: "Try reducing travel or power consumption." };
    } else {
      return { category: "Dangerous Usage", color: "red", advice: "Consider using public transport or reducing appliance usage." };
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!activity || !emission) return;
    const emissionVal = parseFloat(emission);

    const res = await fetch("http://localhost:5000/api/entries", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ activity, emission: emissionVal }),
    });
    const newEntry = await res.json();
    setEntries([newEntry, ...entries]);
    setActivity("");
    setEmission("");

    const risk = getRiskCategory(emissionVal);
    setRiskInfo(risk); // ðŸ‘ˆ show risk box
  };

  const handleLogout = () => {
    localStorage.removeItem("user");
    if (onLogout) onLogout();
    navigate("/login");
  };

  const chartData = entries
    .slice()
    .reverse()
    .map((e) => ({
      date: new Date(e.date).toLocaleDateString(),
      emission: Number(e.emission),
    }));

  return (
    <div className="whole relative" style={{ maxWidth: 600, margin: "40px auto", padding: 24, border: "1px solid #ddd", borderRadius: 8 }}>
      {/* Logout and Title */}
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
        <h1 className="text-2xl font-semibold text-green-700">Carbon Footprint Tracker</h1>
        <button onClick={handleLogout} className="transition hover:bg-blue-500 bg-blue-600 border-2 rounded-xl px-4 py-1 text-white">Logout</button>
      </div>

      {/* Form */}
      <form onSubmit={handleSubmit} className="mt-10 mb-6">
        <input
          className="border-2 rounded-lg ml-2 mr-2 p-2"
          value={activity}
          onChange={(e) => setActivity(e.target.value)}
          placeholder="Activity"
        />
        <input
          className="border-2 rounded-lg ml-2 mr-2 p-2"
          value={emission}
          onChange={(e) => setEmission(e.target.value)}
          type="number"
          placeholder="Emission (kg CO2e)"
          min="0"
        />
        <button type="submit" className="bg-green-600 hover:bg-green-400 text-white rounded-lg px-4 py-2 mt-2">Add Entry</button>
      </form>

      {/* Risk Popup */}
      {riskInfo && (
        <div className="absolute top-0 left-0 right-0 p-4 rounded-md shadow-lg bg-white border mt-2 z-10 mx-4"
          style={{ borderLeft: `6px solid ${riskInfo.color}` }}>
          <div className="flex justify-between items-center">
            <h2 className={`text-lg font-semibold text-${riskInfo.color}-700`}>{riskInfo.category}</h2>
            <button onClick={() => setRiskInfo(null)} className="text-gray-500 hover:text-black">&times;</button>
          </div>
          <p className="text-sm mt-1">{riskInfo.advice}</p>
        </div>
      )}

      {/* Entries */}
      <h2 className="font-semibold text-violet-900 text-xl mt-10">Entries</h2>
      <ul className="mt-2">
        {entries.map((entry) => (
          <li key={entry._id} className="mb-2 border-b pb-2">
            <strong>{new Date(entry.date).toLocaleDateString()}</strong>: {entry.activity} â€” {entry.emission} kg CO<sub>2</sub>e
          </li>
        ))}
        {entries.length === 0 && <li>No entries yet.</li>}
      </ul>

      {/* Chart */}
      <h3 className="text-xl text-gray-900 font-semibold mt-10">Emissions Over Time</h3>
      <ResponsiveContainer width="100%" height={300}>
        <LineChart data={chartData}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="date" />
          <YAxis label={{ value: "kg CO2e", angle: -90, position: "insideLeft" }} />
          <Tooltip />
          <Line type="monotone" dataKey="emission" stroke="#8884d8" />
        </LineChart>
      </ResponsiveContainer>
    </div>
  );
}
