import React, { useEffect, useState } from "react";
import {
  LineChart, Line, XAxis, YAxis, Tooltip, CartesianGrid, ResponsiveContainer
} from "recharts";
import { useNavigate } from "react-router-dom";

export default function Tracker({ onLogout }) {
  const [entries, setEntries] = useState([]);
  const [activity, setActivity] = useState("");
  const [emission, setEmission] = useState("");
  const [riskInfo, setRiskInfo] = useState(null);
  const navigate = useNavigate();

  const user = JSON.parse(localStorage.getItem("user"));

  useEffect(() => {
    if (!user?.id) return;

    fetch(`http://localhost:5000/api/entries?userId=${user.id}`)
      .then((res) => res.json())
      .then((data) => setEntries(data));
  }, [user?.id]);

  const getRiskCategory = (emission) => {
    if (emission < 5) {
      return { category: "Safe Usage", color: "green", advice: "You're doing great! Keep it up." };
    } else if (emission < 10) {
      return { category: "Medium Risk Usage", color: "orange", advice: "Try reducing travel or power consumption." };
    } else {
      return { category: "Dangerous Usage", color: "red", advice: "Consider using public transport or reducing appliance usage." };
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!activity || !emission || !user?.id) return;

    const emissionVal = parseFloat(emission);

    const res = await fetch("http://localhost:5000/api/entries", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ activity, emission: emissionVal, userId: user.id }),
    });

    const newEntry = await res.json();
    setEntries([newEntry, ...entries]);
    setActivity("");
    setEmission("");

    const risk = getRiskCategory(emissionVal);
    setRiskInfo(risk);
  };

  const handleLogout = () => {
    localStorage.removeItem("user");
    if (onLogout) onLogout();
    navigate("/login");
  };

  const chartData = entries
    .slice()
    .reverse()
    .map((e) => ({
      date: new Date(e.date).toLocaleDateString(),
      emission: Number(e.emission),
    }));

  return (
    <div className="relative max-w-2xl mx-auto mt-10 p-6 border border-gray-300 rounded-xl bg-white shadow-md">
      {/* Top bar */}
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold text-green-700">Carbon Footprint Tracker</h1>
        <button onClick={handleLogout} className="bg-blue-600 text-white px-4 py-1 rounded-lg hover:bg-blue-500">
          Logout
        </button>
      </div>

      {/* Form */}
      <form onSubmit={handleSubmit} className="space-x-2 mb-6">
        <input
          className="border rounded-lg px-2 py-1"
          value={activity}
          onChange={(e) => setActivity(e.target.value)}
          placeholder="Activity"
        />
        <input
          className="border rounded-lg px-2 py-1"
          value={emission}
          onChange={(e) => setEmission(e.target.value)}
          type="number"
          placeholder="Emission (kg CO₂e)"
          min="0"
        />
        <button type="submit" className="bg-green-600 text-white px-4 py-1 rounded-lg hover:bg-green-500">
          Add Entry
        </button>
      </form>

      {/* Risk Box */}
      {riskInfo && (
        <div className="absolute top-2 left-2 right-2 bg-white border-l-8 rounded-md p-4 shadow z-20"
             style={{ borderColor: riskInfo.color }}>
          <div className="flex justify-between">
            <h2 className={`text-lg font-semibold text-${riskInfo.color}-700`}>
              {riskInfo.category}
            </h2>
            <button onClick={() => setRiskInfo(null)} className="text-gray-600 hover:text-black">&times;</button>
          </div>
          <p className="text-sm mt-1">{riskInfo.advice}</p>
        </div>
      )}

      {/* Entries */}
      <h2 className="text-xl font-semibold text-gray-800 mb-2 mt-8">Entries</h2>
      <ul className="space-y-2">
        {entries.length === 0 && <li>No entries yet.</li>}
        {entries.map((entry) => (
          <li key={entry._id} className="border-b pb-2 text-sm">
            <strong>{new Date(entry.date).toLocaleDateString()}</strong>: {entry.activity} — {entry.emission} kg CO₂e
          </li>
        ))}
      </ul>

      {/* Chart */}
      <h3 className="text-lg font-semibold text-gray-900 mt-10 mb-2">Emissions Over Time</h3>
      <ResponsiveContainer width="100%" height={300}>
        <LineChart data={chartData}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="date" />
          <YAxis label={{ value: "kg CO₂e", angle: -90, position: "insideLeft" }} />
          <Tooltip />
          <Line type="monotone" dataKey="emission" stroke="#10b981" />
        </LineChart>
      </ResponsiveContainer>
    </div>
  );
}
