import React, { useEffect, useState } from "react";

function App() {
  const [entries, setEntries] = useState([]);
  const [activity, setActivity] = useState("");
  const [emission, setEmission] = useState("");

  // Fetch entries from backend on mount
  useEffect(() => {
    fetch("http://localhost:5000/api/entries")
      .then((res) => res.json())
      .then((data) => setEntries(data));
  }, []);

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!activity || !emission) return;
    const res = await fetch("http://localhost:5000/api/entries", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ activity, emission }),
    });
    const newEntry = await res.json();
    setEntries([newEntry, ...entries]);
    setActivity("");
    setEmission("");
  };

  return (
    <div style={{ maxWidth: 600, margin: "40px auto", padding: 24, border: "1px solid #ddd", borderRadius: 8 }}>
      <h1>Carbon Footprint Tracker</h1>
      <form onSubmit={handleSubmit} style={{ marginBottom: 24 }}>
        <input
          value={activity}
          onChange={(e) => setActivity(e.target.value)}
          placeholder="Activity"
          style={{ marginRight: 8, padding: 6 }}
        />
        <input
          value={emission}
          onChange={(e) => setEmission(e.target.value)}
          type="number"
          placeholder="Emission (kg CO2e)"
          style={{ marginRight: 8, padding: 6 }}
          min="0"
        />
        <button type="submit" style={{ padding: "6px 14px" }}>Add Entry</button>
      </form>
      <h2>Entries</h2>
      <ul style={{ listStyle: "none", padding: 0 }}>
        {entries.map((entry) => (
          <li key={entry._id} style={{ marginBottom: 10, borderBottom: "1px solid #eee", paddingBottom: 8 }}>
            <strong>{new Date(entry.date).toLocaleDateString()}</strong>: {entry.activity} â€” {entry.emission} kg CO2e
          </li>
        ))}
        {entries.length === 0 && <li>No entries yet.</li>}
      </ul>
    </div>
  );
}

export default App;
