import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";

export default function Register() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleRegister = async (e) => {
    e.preventDefault();
    const res = await fetch("http://localhost:5000/api/register", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ email, password }),
    });
    if (res.ok) {
      navigate("/login");
    } else {
      setError("Registration failed");
    }
  };
  return (
    <div style={{ maxWidth: 400, margin: "40px auto" }}>
      <h2>Register</h2>
      <form onSubmit={handleRegister}>
        <input placeholder="Email" value={email} onChange={e=>setEmail(e.target.value)} type="email" required style={{ display: "block", marginBottom: 10, width: "100%" }}/>
        <input placeholder="Password" value={password} onChange={e=>setPassword(e.target.value)} type="password" required style={{ display: "block", marginBottom: 10, width: "100%" }}/>
        <button type="submit" style={{ width: "100%" }}>Register</button>
      </form>
      {error && <div style={{ color: "red" }}>{error}</div>}
      <div style={{marginTop: 10}}>Already have an account? <Link to="/login">Login</Link></div>
    </div>
  );
}
